/**
 * Zepto picLazyLoad Plugin
 */
LazyLoadPic = function () {
    var $this = null,
        _winScrollTop = 0,
        isEnable = false;
    _winHeight = $(window).height();

    var settings = $.extend({
        threshold: 0, // 提前高度加载
        placeholder: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsQAAA7EAZUrDhsAAAANSURBVBhXYzh8+PB/AAffA0nNPuCLAAAAAElFTkSuQmCC'
    }, settings || {});


    // 滚动触发换图
    $(window).on('scroll', function () {
        _winScrollTop = $(window).scrollTop();
        if(isEnable){
            lazyLoadPic();
        }
    });

    // 懒加载图片
    function lazyLoadPic() {
        $this.each(function () {
            var $self = $(this);
            var _offsetTop;
            // 如果是img
            if ($self.is('img')) {
                if ($self.attr('data-original')) {
                    _offsetTop = $self.offset().top;
                    if ((_offsetTop - settings.threshold) <= (_winHeight + _winScrollTop)) {
                        $self.attr('src', $self.attr('data-original'));
                        $self.removeAttr('data-original');
                        $self.addClass('lazy-loaded');
                    }
                }
                // 如果是背景图
            } else {
                if ($self.attr('data-original')) {
                    // 默认占位图片
                    if ($self.css('background-image') == 'none') {
                        $self.css('background-image', 'url(' + settings.placeholder + ')');
                    }
                    _offsetTop = $self.offset().top;
                    if ((_offsetTop - settings.threshold) <= (_winHeight + _winScrollTop)) {
                        $self.css('background-image', 'url(' + $self.attr('data-original') + ')');
                        $self.removeAttr('data-original');
                        $self.addClass('lazy-loaded');
                    }
                }
            }
        });
    }

    return {
        initLazy: function (p_obj, settings) {
            $this = p_obj;
            isEnable = true;
            settings = $.extend({
                threshold: 0, // 提前高度加载
                placeholder: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsQAAA7EAZUrDhsAAAANSURBVBhXYzh8+PB/AAffA0nNPuCLAAAAAElFTkSuQmCC'
            }, settings || {});
            // 执行懒加载图片
            lazyLoadPic();
        },
        disableLazy:function(){
            isEnable = false;
        }
    }
}
var g_lazyLoad = LazyLoadPic();